mapper.insert:
insert into td_rms_user_channel (CHANNEL_ID, USER_ID, ISP,TYPE
)
values (#{channelId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER},
#{isp,jdbcType=VARCHAR},#{type,jdbcType=VARCHAR}
)

mapper.select:
select a.CHANNEL_ID, a.USER_ID, a.ISP,a.TYPE,b.INTERFACE_NAME from td_rms_user_channel a,td_rms_channel b

		a.CHANNEL_ID = b.CHANNEL_ID 
	
		and a.CHANNEL_ID =#{channelId,jdbcType=INTEGER}
	
	
		and a.USER_ID = #{userId,jdbcType=INTEGER}
	
	
		and a.ISP = #{isp,jdbcType=VARCHAR}
	
	
		and a.TYPE = #{type,jdbcType=VARCHAR}

mapper.delete:
delete from td_rms_user_channel

	
		CHANNEL_ID =#{channelId,jdbcType=INTEGER}
	
	
		and USER_ID = #{userId,jdbcType=INTEGER}
